import java.nio.file.Files;
import java.nio.file.Paths;

class DataProvider{
     def log
     def context
     def testRunner

     def DataProvider(log, context, testRunner) {
        this.log = log
        this.context = context
        this.testRunner = testRunner
        }


     def runWithOneWord(request, wordToCheck, expectedWord) {
     	def status = testRunner.runTestStepByName(request.name).getStatus().toString();

     	testRunner.testCase.testSuite.project.setPropertyValue("wordToCheck",wordToCheck);
		testRunner.testCase.testSuite.project.setPropertyValue("expectedWord",expectedWord);


     	def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
     	def responseHolder =  groovyUtils.getXmlHolder(testRunner.testCase.testSteps[request.name].testRequest.response.getContentAsXml());
     	responseHolder.namespaces["sp"] = "http://speller.yandex.net/services/spellservice";
     	def actualWord;

     	for( item in responseHolder.getNodeValues( "//sp:s" )){
     		if(item.equals(expectedWord)) {
     			actualWord=item;
     			break;
     			}
     	}

		log.info("		word to check was: ["+ wordToCheck + "]");
		log.info("		expected word was: ["+ expectedWord + "]");
		log.info("		actual word is: [" + actualWord + "]");
		if (status=='OK' && actualWord.equals(expectedWord)){
				log.info("     >>>  TEST PASSED");
		     }else{
				testRunner.fail(" WRONG RESPONSE");
				log.info("      !!!TEST FAILED!!!");
			}

     	}

     	def runWithManyWords(request, wordsToCheck, expectedWords) {
     		def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
     		def responseHolder =  groovyUtils.getXmlHolder(testRunner.testCase.testSteps[request.name].testRequest.response.getContentAsXml());
     		responseHolder.namespaces["sp"] = "http://speller.yandex.net/services/spellservice";
     		testRunner.testCase.testSuite.project.setPropertyValue("wordsToCheck",wordsToCheck);
     		int i = 0;
     		for( item in responseHolder.getNodeValues( "//sp:s" )){
     			def status = testRunner.runTestStepByName(request.name).getStatus().toString();
     			def wordsFromString = wordsToCheck.split();
				testRunner.testCase.testSuite.project.setPropertyValue("expectedWord",expectedWords[i]);

				log.info("		word to check was: ["+ wordsFromString[i] + "]");
				log.info("		expected word was: ["+ expectedWords[i] + "]");
				log.info("		actual word is: [" + item + "]");
				if (status=='OK' && item.equals(expectedWords[i])){
					log.info("     >>>  TEST PASSED");
		    		 }else{
					testRunner.fail("WRONG RESPONSE");
					log.info("      !!!TEST FAILED!!!");
					}
					i++;
     		}

   	  }

}


context.setProperty( "DataProvider", new DataProvider(log, context, testRunner) )

def wordToCheck = testRunner.testCase.getPropertyValue("wordToCheck")
def expectedWord =  testRunner.testCase.getPropertyValue("expectedWord")
def nameOfRequest = context.testCase.getTestStepByName("ruOneWordCheck")

project = testRunner.testCase.testSuite.project;
tcase = project.testSuites["DataProviderSetUp"].testCases["setUp"];
dataProvider = tcase.getTestStepByName("DataProvider").run(testRunner, context);

testRunner.testCase.testSuite.project.setPropertyValue("wordToCheck",wordToCheck);

context.DataProvider.runWithOneWord(nameOfRequest, wordToCheck, expectedWord)

