import java.nio.file.Files;
import java.nio.file.Paths;

class DataProvider{
     def log
     def context
     def testRunner

     def DataProvider(log, context, testRunner) {
        this.log = log
        this.context = context
        this.testRunner = testRunner
        }


   	  def runWithManyWords(request, wordsToCheck, expectedWords) {
     		def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
     		def responseHolder =  groovyUtils.getXmlHolder(testRunner.testCase.testSteps[request.name].testRequest.response.getContentAsXml());
     		responseHolder.namespaces["sp"] = "http://speller.yandex.net/services/spellservice";
     		String checkingWords = wordsToCheck.join(",");
     		testRunner.testCase.testSuite.project.setPropertyValue("wordsToCheck",checkingWords);
     		def actualWord;
     		for(int i = 0; i<wordsToCheck.size(); i++){
     			def status = testRunner.runTestStepByName(request.name).getStatus().toString();
     			def wordsFromString = checkingWords.split(",");
     			testRunner.testCase.testSuite.project.setPropertyValue("expectedWord",expectedWords[i]);
				log.info("		word to check was: ["+ wordsFromString[i] + "]");
				log.info("		expected word was: ["+ expectedWords[i] + "]");
     			for( item in responseHolder.getNodeValues( "//sp:s" )){

     				if(item.equals(expectedWords[i])) {
     					actualWord=item;
     					break;
     					}		
     				}

					if (status=='OK' && actualWord.equals(expectedWords[i])){
						log.info("		actual word is: [" + actualWord + "]");
						log.info("     >>>  TEST PASSED");
		    			 }else{
		    			 	log.info("		actual word is: [" + actualWord + "]");
						testRunner.fail("WRONG RESPONSE");
						log.info("      !!!TEST FAILED!!!");
					}
					actualWord=null;

     		}

   	  }


   	   def runWithManyWordsForFile(request, wordsToCheck, expectedWords) {
     		def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
     		def responseHolder =  groovyUtils.getXmlHolder(testRunner.testCase.testSteps[request.name].testRequest.response.getContentAsXml());
     		responseHolder.namespaces["sp"] = "http://speller.yandex.net/services/spellservice";
     		def actualWord;
     		def checkingWords = wordsToCheck.split(",");
     		def expWords = expectedWords.split(",");
     		for(int i = 0; i<checkingWords.size(); i++){
     			def status = testRunner.runTestStepByName(request.name).getStatus().toString();
				log.info("		word to check was: ["+ checkingWords[i] + "]");
				log.info("		expected word was: ["+ expWords[i] + "]");
     			for( item in responseHolder.getNodeValues( "//sp:s" )){

     				if(item.equals(expWords[i])) {
     					actualWord=item;
     					break;
     					}		
     				}

					if (status=='OK' && actualWord.equals(expWords[i])){
						log.info("		actual word is: [" + actualWord + "]");
						log.info("     >>>  TEST PASSED");
		    			 }else{
		    			 	log.info("		actual word is: [" + actualWord + "]");
						testRunner.fail("WRONG RESPONSE");
						log.info("      !!!TEST FAILED!!!");
					}
					actualWord=null;

     		}

   	  }

}


context.setProperty( "DataProvider", new DataProvider(log, context, testRunner) )

def wordsToCheck = Arrays.asList("писок","мнсто","сонце", "hully", "hillo");
def expectedWords =  Arrays.asList("песок","место","солнце", "holly", "hello");
def nameOfRequest= context.testCase.getTestStepByName("oneWordCheck")

project = testRunner.testCase.testSuite.project;
tcase = project.testSuites["DataProviderSetUp"].testCases["setUp"];
dataProvider = tcase.getTestStepByName("DataProvider").run(testRunner, context);


context.DataProvider.runWithManyWords(nameOfRequest, wordsToCheck, expectedWords);

