con:testCase.con:setupScript:
	def filename = testRunner.testCase.testSuite.project.getPropertyValue("filePath")
	File file = new File(filename)
	def checking = [];
	def expecting = []; 
	def lines = file.readLines()
	for (line in lines) {
  		 wordToCheck = line.split()[0];
		expectedWord = line.split()[1];
		checking.add(wordToCheck);
		expecting.add(expectedWord);
	}
	testRunner.testCase.testSuite.project.setPropertyValue("wordFromFileToCheck",checking.join(","));
	testRunner.testCase.testSuite.project.setPropertyValue("expectedFromFileWord",expecting.join(","));

